import{_ as c}from"./plugin-vue_export-helper-c27b6911.js";import{r as d,o as l,c as n,a as e,b as o,d as a,e as i}from"./app-4fbdbfa5.js";const r={},h=i('<h2 id="_1-1-netty-的介绍" tabindex="-1"><a class="header-anchor" href="#_1-1-netty-的介绍" aria-hidden="true">#</a> 1.1 Netty 的介绍</h2><ol><li><code>Netty</code> 是由 <code>JBOSS</code> 提供的一个 <code>Java</code> 开源框架，现为 <code>Github</code> 上的独立项目。</li><li><code>Netty</code> 是一个异步的、基于事件驱动的网络应用框架，用以快速开发高性能、高可靠性的网络 <code>IO</code> 程序。</li><li><code>Netty</code> 主要针对在 <code>TCP</code> 协议下，面向 <code>Client</code> 端的高并发应用，或者 <code>Peer-to-Peer</code> 场景下的大量数据持续传输的应用。</li><li><code>Netty</code> 本质是一个 <code>NIO</code> 框架，适用于服务器通讯相关的多种应用场景。</li><li>要透彻理解 <code>Netty</code>，需要先学习 <code>NIO</code>，这样我们才能阅读 <code>Netty</code> 的源码。</li></ol><h2 id="_1-2-netty-的应用场景" tabindex="-1"><a class="header-anchor" href="#_1-2-netty-的应用场景" aria-hidden="true">#</a> 1.2 Netty 的应用场景</h2><h3 id="_1-2-1-互联网行业" tabindex="-1"><a class="header-anchor" href="#_1-2-1-互联网行业" aria-hidden="true">#</a> 1.2.1 互联网行业</h3><ol><li>互联网行业：在分布式系统中，各个节点之间需要远程服务调用，高性能的 <code>RPC</code> 框架必不可少，<code>Netty</code> 作为异步高性能的通信框架，往往作为基础通信组件被这些 <code>RPC</code> 框架使用。</li><li>典型的应用有：阿里分布式服务框架 <code>Dubbo</code> 的 <code>RPC</code> 框 架使用 <code>Dubbo</code> 协议进行节点间通信，<code>Dubbo</code> 协议默认使用 <code>Netty</code> 作为基础通信组件，用于实现各进程节点之间的内部通信。</li></ol><p><img src="https://javablog-image.oss-cn-hangzhou.aliyuncs.com/blog/chapter01_01.png" alt="" loading="lazy"></p><h3 id="_1-2-2-游戏行业" tabindex="-1"><a class="header-anchor" href="#_1-2-2-游戏行业" aria-hidden="true">#</a> 1.2.2 游戏行业</h3><ol><li>无论是手游服务端还是大型的网络游戏，<code>Java</code> 语言得到了越来越广泛的应用。</li><li><code>Netty</code> 作为高性能的基础通信组件，提供了 <code>TCP/UDP</code> 和 <code>HTTP</code> 协议栈，方便定制和开发私有协议栈，账号登录服务器。</li><li>地图服务器之间可以方便的通过 <code>Netty</code> 进行高性能的通信。</li></ol><p><img src="https://javablog-image.oss-cn-hangzhou.aliyuncs.com/blog/chapter01_02.png" alt="" loading="lazy"> <img src="https://javablog-image.oss-cn-hangzhou.aliyuncs.com/blog/chapter01_03.png" alt="" loading="lazy"></p><h3 id="_1-2-3-大数据领域" tabindex="-1"><a class="header-anchor" href="#_1-2-3-大数据领域" aria-hidden="true">#</a> 1.2.3 大数据领域</h3><ol><li>经典的 <code>Hadoop</code> 的高性能通信和序列化组件 <code>Avro</code> 的 <code>RPC</code> 框架，默认采用 <code>Netty</code> 进行跨界点通信。</li><li>它的 <code>NettyService</code> 基于 <code>Netty</code> 框架二次封装实现。</li></ol><p><img src="https://javablog-image.oss-cn-hangzhou.aliyuncs.com/blog/chapter01_04.png" alt="" loading="lazy"></p><h3 id="_1-2-4-其它开源项目使用到-netty" tabindex="-1"><a class="header-anchor" href="#_1-2-4-其它开源项目使用到-netty" aria-hidden="true">#</a> 1.2.4 其它开源项目使用到 Netty</h3>',13),s={href:"https://netty.io/wiki/related-projects.html",target:"_blank",rel:"noopener noreferrer"},g=e("p",null,[e("img",{src:"https://javablog-image.oss-cn-hangzhou.aliyuncs.com/blog/chapter01_05.png",alt:"",loading:"lazy"})],-1),_=e("h2",{id:"_1-4-netty-的学习资料参考",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_1-4-netty-的学习资料参考","aria-hidden":"true"},"#"),o(" 1.4 Netty 的学习资料参考")],-1),p={href:"https://item.jd.com/12070975.html",target:"_blank",rel:"noopener noreferrer"},y=e("img",{src:"https://javablog-image.oss-cn-hangzhou.aliyuncs.com/blog/59141c1dN7a93c127.jpg",alt:"",loading:"lazy"},null,-1),m={href:"https://item.jd.com/11681556.html",target:"_blank",rel:"noopener noreferrer"},u=e("img",{src:"https://javablog-image.oss-cn-hangzhou.aliyuncs.com/blog/555080b2Ne6cbf9e3.jpg",alt:"",loading:"lazy"},null,-1);function b(N,f){const t=d("ExternalLinkIcon");return l(),n("div",null,[h,e("p",null,[o("网址："),e("a",s,[o("https://netty.io/wiki/related-projects.html"),a(t)])]),g,_,e("p",null,[e("a",p,[y,a(t)]),o(),e("a",m,[u,a(t)])])])}const j=c(r,[["render",b],["__file","chapter01.html.vue"]]);export{j as default};
