import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o as r,c as i,a as e,b as o,d,e as t}from"./app-9a9d33f6.js";const l={},s=t('<h2 id="_4-1-原生-nio-存在的问题" tabindex="-1"><a class="header-anchor" href="#_4-1-原生-nio-存在的问题" aria-hidden="true">#</a> 4.1 原生 NIO 存在的问题</h2><ol><li><code>NIO</code> 的类库和 <code>API</code> 繁杂，使用麻烦：需要熟练掌握 <code>Selector</code>、<code>ServerSocketChannel</code>、<code>SocketChannel</code>、<code>ByteBuffer</code>等。</li><li>需要具备其他的额外技能：要熟悉 <code>Java</code> 多线程编程，因为 <code>NIO</code> 编程涉及到 <code>Reactor</code> 模式，你必须对多线程和网络编程非常熟悉，才能编写出高质量的 <code>NIO</code> 程序。</li><li>开发工作量和难度都非常大：例如客户端面临断连重连、网络闪断、半包读写、失败缓存、网络拥塞和异常流的处理等等。4. <code>JDK NIO</code> 的 <code>Bug</code>：例如臭名昭著的 <code>Epoll Bug</code>，它会导致 <code>Selector</code> 空轮询，最终导致 <code>CPU100%</code>。直到 <code>JDK1.7</code> 版本该问题仍旧存在，没有被根本解决。</li></ol><h2 id="_4-2-netty-官网说明" tabindex="-1"><a class="header-anchor" href="#_4-2-netty-官网说明" aria-hidden="true">#</a> 4.2 Netty 官网说明</h2>',3),h={href:"https://netty.io/",target:"_blank",rel:"noopener noreferrer"},_=t('<p>Netty is an asynchronous event-driven network application framework for rapid development of maintainable high performance protocol servers &amp; clients.</p><figure><img src="https://javablog-image.oss-cn-hangzhou.aliyuncs.com/blog/chapter04_01.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><h2 id="_4-3-netty-的优点" tabindex="-1"><a class="header-anchor" href="#_4-3-netty-的优点" aria-hidden="true">#</a> 4.3 Netty 的优点</h2><p><code>Netty</code> 对 <code>JDK</code> 自带的 <code>NIO</code> 的 <code>API</code> 进行了封装，解决了上述问题。</p><ol><li>设计优雅：适用于各种传输类型的统一 <code>API</code> 阻塞和非阻塞 <code>Socket</code>；基于灵活且可扩展的事件模型，可以清晰地分离关注点；高度可定制的线程模型-单线程，一个或多个线程池。</li><li>使用方便：详细记录的 <code>Javadoc</code>，用户指南和示例；没有其他依赖项，<code>JDK5（Netty3.x）</code>或 <code>6（Netty4.x）</code>就足够了。</li><li>高性能、吞吐量更高：延迟更低；减少资源消耗；最小化不必要的内存复制。</li><li>安全：完整的 <code>SSL/TLS</code> 和 <code>StartTLS</code> 支持。</li><li>社区活跃、不断更新：社区活跃，版本迭代周期短，发现的 <code>Bug</code> 可以被及时修复，同时，更多的新功能会被加入。</li></ol><h2 id="_4-4-netty-版本说明" tabindex="-1"><a class="header-anchor" href="#_4-4-netty-版本说明" aria-hidden="true">#</a> 4.4 Netty 版本说明</h2>',6),y=t("<li><code>Netty</code> 版本分为 <code>Netty 3.x</code> 和 <code>Netty 4.x</code>、<code>Netty 5.x</code></li><li>因为 <code>Netty 5</code> 出现重大 <code>bug</code>，已经被官网废弃了，目前推荐使用的是 <code>Netty 4.x</code>的稳定版本</li><li>目前在官网可下载的版本 <code>Netty 3.x</code>、<code>Netty 4.0.x</code> 和 <code>Netty 4.1.x</code></li><li>在本套课程中，我们讲解 <code>Netty4.1.x</code> 版本</li>",4),p=e("code",null,"Netty",-1),N={href:"https://bintray.com/netty/downloads/netty/",target:"_blank",rel:"noopener noreferrer"};function f(u,x){const c=n("ExternalLinkIcon");return r(),i("div",null,[s,e("p",null,[o("官网："),e("a",h,[o("https://netty.io/"),d(c)])]),_,e("ol",null,[y,e("li",null,[p,o(" 下载地址："),e("a",N,[o("https://bintray.com/netty/downloads/netty/"),d(c)])])])])}const b=a(l,[["render",f],["__file","chapter04.html.vue"]]);export{b as default};
