import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as i,e as t}from"./app.9106d0d0.js";const l={},r=t('<h2 id="gateway-简介" tabindex="-1"><a class="header-anchor" href="#gateway-简介" aria-hidden="true">#</a> GateWay 简介</h2><blockquote><p>Spring Cloud Gateway 是 Spring 官方基于 Spring 5.0，Spring Boot 2.0 和 Project Reactor 等技术开发的网关，Spring Cloud Gateway 旨在为微服务架构提供一种简单而有效的统一的 API 路由管理方式。Spring Cloud Gateway 作为 Spring Cloud 生态系中的网关，目标是替代 Zuul，其不仅提供统一的路由方式，并且基于 Filter链的方式提供了网关基本的功能，例如：安全，监控/埋点，和限流等。</p></blockquote><h3 id="特点" tabindex="-1"><a class="header-anchor" href="#特点" aria-hidden="true">#</a> 特点</h3><ul><li>限流</li><li>路径重写</li><li>动态路由</li><li>集成 Spring Cloud DiscoveryClient</li><li>集成Hystrix断路器</li></ul><p>和Zuul 对比：</p><ol><li><p>Zuul 是 Netﬂix 公司的开源产品，Spring Cloud Gateway 是 Spring 家族中的产品，可以和Spring 家族中的其他组件更好的融合。</p></li><li><p>Zuul1 不支持长连接，例如 websocket。</p></li><li><p>Spring Cloud Gateway 支持限流。</p></li><li><p>Spring Cloud Gateway 基于 <code>Netty</code> 来开发，实现了异步和非阻塞，占用资源更小，性能强于Zuul。</p></li></ol><h3 id="gateway中的-几个概念" tabindex="-1"><a class="header-anchor" href="#gateway中的-几个概念" aria-hidden="true">#</a> GateWay中的 几个概念</h3><p><img src="https://cdn.jsdelivr.net/gh/itmarico/image-repository/img/aHR0cDovL2Nvcy5yYWluMTAyNC5jb20vbWFya2Rvd24vaW1hZ2UtMjAxOTEwMDgxNjA3MTM4MjIucG5n" alt="img" loading="lazy"></p><h2 id="快速开始" tabindex="-1"><a class="header-anchor" href="#快速开始" aria-hidden="true">#</a> 快速开始</h2>',9),o=[r];function n(d,c){return e(),i("div",null,o)}const h=a(l,[["render",n],["__file","gateway.html.vue"]]);export{h as default};
